CREATE TABLE `user` (`id` int(11) NOT NULL AUTO_INCREMENT, `username` varchar(255) NOT NULL, PRIMARY KEY (`id`),  UNIQUE KEY `username` (`username`)) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE `address` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `ip_address` varchar(16) NOT NULL, PRIMARY KEY (`id`),   UNIQUE KEY `ip_address` (`ip_address`)) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE `addressData` (  `id` int(11) NOT NULL AUTO_INCREMENT,   `ip_address_id` int(11) NOT NULL, `fail_count` int(11) NOT NULL, `success_count` int(11) NOT NULL,   `dateOccurred_from` DATETIME,     `dateOccurred_to` DATETIME,  `metric_set` TINYINT(1) DEFAULT 0,    PRIMARY KEY (`id`),    FOREIGN KEY (`ip_address_id`) references address(id)) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE `userData` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `user_id` int(11) NOT NULL,    `fail_count` int(11) NOT NULL, `success_count` int(11) NOT NULL,     `dateOccurred_from` DATETIME,     `dateOccurred_to` DATETIME,    `metric_set` TINYINT(1) DEFAULT 0 ,   PRIMARY KEY (`id`),  FOREIGN KEY (`user_id`) references user(id)) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE `anomaly` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `data_id` int(11) NOT NULL,  `value` decimal(11,4) NOT NULL,  `level_id` int(11) NOT NULL, `type` TINYINT(1) NOT NULL,  PRIMARY KEY (`id`));
CREATE TABLE `blockingAccount` (`id` int(11) NOT NULL AUTO_INCREMENT, `user_id` int(11) NOT NULL, date_blocked DATETIME NOT NULL, date_unblocked DATETIME NOT NULL, status varchar(255) DEFAULT 'blocked', PRIMARY KEY (`id`), FOREIGN KEY (`user_id`) references user(id)) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE `blockingAddress` (`id` int(11) NOT NULL AUTO_INCREMENT, `ip_address_id` int(11) NOT NULL, date_blocked DATETIME NOT NULL, date_unblocked DATETIME NOT NULL, status varchar(255) DEFAULT 'blocked', PRIMARY KEY (`id`), FOREIGN KEY (`ip_address_id`) references address(id)) ENGINE=InnoDB DEFAULT CHARSET=latin1;